import{_ as s,o as a,c as n,N as e}from"./chunks/framework.60272d8a.js";const b=JSON.parse('{"title":"deepin(Linux)中安装git,生成SSH公私钥配置Gitee，GitHub(2020.11)","description":"","frontmatter":{},"headers":[],"relativePath":"knowledge/Deepin/生成SSH公私钥配置.md","lastUpdated":1706686109000}'),l={name:"knowledge/Deepin/生成SSH公私钥配置.md"},p=e(`<h1 id="deepin-linux-中安装git-生成ssh公私钥配置gitee-github-2020-11" tabindex="-1">deepin(Linux)中安装git,生成SSH公私钥配置Gitee，GitHub(2020.11) <a class="header-anchor" href="#deepin-linux-中安装git-生成ssh公私钥配置gitee-github-2020-11" aria-label="Permalink to &quot;deepin(Linux)中安装git,生成SSH公私钥配置Gitee，GitHub(2020.11)&quot;">​</a></h1><blockquote><p>Linux下就不要再费劲去找win下的那种图形化工具一直“下一步”“下一步”的去安装了（ps：官方教程上也是用的命令行） 今天需要用到gitee,然后查找各种教程安装，博客视频都有看，因为说的都有些区别，花费了好长时间对比，所以想总结一个完整的，以免看到一大堆不太一样的教程再去纠结选哪个。</p></blockquote><h2 id="_1-输入命令安装git" tabindex="-1">1. 输入命令安装git <a class="header-anchor" href="#_1-输入命令安装git" aria-label="Permalink to &quot;1. 输入命令安装git&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">sudo</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">apt</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">install</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">git-all</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><h2 id="_2-然后进行使用git之前需要做的最小配置" tabindex="-1">2. 然后进行使用git之前需要做的最小配置 <a class="header-anchor" href="#_2-然后进行使用git之前需要做的最小配置" aria-label="Permalink to &quot;2. 然后进行使用git之前需要做的最小配置&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">config</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">--global</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">user.name</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">your_name</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#FFCB6B;">git</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">config</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">--global</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">user.email</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">xxxxx@youremail.com</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><h2 id="_3-生成ssh公私钥" tabindex="-1">3. 生成SSH公私钥 <a class="header-anchor" href="#_3-生成ssh公私钥" aria-label="Permalink to &quot;3. 生成SSH公私钥&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#BABED8;"> </span><span style="color:#FFCB6B;">ssh-keygen</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-t</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">rsa</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-b</span><span style="color:#BABED8;"> </span><span style="color:#F78C6C;">4096</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-C</span><span style="color:#BABED8;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">xxxx@youremail.com</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><blockquote><p>然后会有3条命令提示，直接回车确认就好</p></blockquote><h2 id="_4-然后进入-ssh文件夹" tabindex="-1">4. 然后进入.ssh文件夹 <a class="header-anchor" href="#_4-然后进入-ssh文件夹" aria-label="Permalink to &quot;4. 然后进入.ssh文件夹&quot;">​</a></h2><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">~/.ssh</span></span>
<span class="line"><span style="color:#FFCB6B;">ls</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-al</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br></div></div><p>以下两个文件即为公私钥</p><div class="language-text line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">text</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#babed8;">id_rsa #私钥</span></span>
<span class="line"><span style="color:#babed8;">id_rsa.pub #公钥</span></span>
<span class="line"><span style="color:#babed8;"></span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>执行命令</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">cat</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">id_rsa.pub</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>然后一大串的那个即为公钥，复制粘贴到gitee,github等即可配置完成</p><p>配置好公私钥之后 输入命令测试是否配置成功</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">ssh</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">-T</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">git@gitee.com</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br></div></div><p>第一次测试会提示是否继续连接，输入yes,之后再进行就不会了在询问了 如该测试失败提示<code>Permission denied, please try again.</code>，则仔细看下方的注意点，照做</p><h2 id="注意点" tabindex="-1">注意点 <a class="header-anchor" href="#注意点" aria-label="Permalink to &quot;注意点&quot;">​</a></h2><p>使用公钥(id_rsa.pub)链接线上仓库认证，本地GIT仓库也需要使用ssh链接方式 如该配置了公钥，pull代码还需要输入帐号密码，则去当前本地仓库根目录找到.git &gt; config 文件修改以下内容</p><div class="language-shell line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#676E95;font-style:italic;"># 找到[remote &quot;origin&quot;] 这一行</span></span>
<span class="line"><span style="color:#89DDFF;">[</span><span style="color:#BABED8;">remote </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">origin</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span></span>
<span class="line"><span style="color:#BABED8;">	</span><span style="color:#FFCB6B;">url</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">=</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">root@gitxxxxx.git</span></span>
<span class="line"><span style="color:#BABED8;">	</span><span style="color:#FFCB6B;">fetch</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">=</span><span style="color:#BABED8;"> </span><span style="color:#C3E88D;">+refs/heads/</span><span style="color:#BABED8;">*</span><span style="color:#C3E88D;">:refs/remotes/origin/</span><span style="color:#BABED8;">*</span></span>
<span class="line"><span style="color:#BABED8;">	</span></span>
<span class="line"><span style="color:#676E95;font-style:italic;"># 修改url的仓库地址为ssh地址</span></span>
<span class="line"></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br></div></div><p><a href="https://docs.github.com/cn/authentication/connecting-to-github-with-ssh/testing-your-ssh-connection" target="_blank" rel="noreferrer">git密钥教程</a></p>`,23),o=[p];function t(r,i,c,d,u,y){return a(),n("div",null,o)}const B=s(l,[["render",t]]);export{b as __pageData,B as default};
